###########################ldsc遗传相关性###########################


###########################step1.sumstats_result###########################
#用munge_sumstats进行处理#
conda activate ldsc

##############################批量写循环（first order）##############################
cd ./step1_sumstats_result
# 设置输入路径
input_dir="./Assoc_Lung_phenotype_low_3.8w_clean"

# 获取文件列表
file_list=$(ls "$input_dir"/original_firstorder_*_assoc.result)

# 循环处理每个文件
for file in $file_list; do
    # 提取文件名（去除路径和文件扩展名）
    filename=$(basename "$file" _assoc.result)

    # 提取指定列并重命名列名，输出到临时文件
    awk -F',' 'BEGIN{OFS="\t"; print "CHR", "BP", "A1", "A2", "BETA", "P", "FRQ", "INFO", "SNP"} NR>1 {printf "%d\t%d\t%s\t%s\t%.6f\t%.200f\t%.3f\t%.3f\t%s\n", $2, $3, $5, $6, $11+0, $16+0, $7+0, $8+0, $18}' "$file" \
    > output.txt

    # 设置输出文件名
    output_name="${filename}_munge"

    # 运行 munge_sumstats.py 命令
    ./ldsc-master/munge_sumstats.py --N 35469 --info-min 0.3 --sumstats output.txt --out "${output_name}"

    # 删除临时文件
    rm output.txt
done


##############################批量写循环（shape）##############################
cd ./step1_sumstats_result
# 设置输入路径
input_dir2="./Assoc_Lung_phenotype_low_3.8w_clean"

